      setTimeout(() => setShow(1), firstDuration);
    }
  }, [show]);

  return (
    <div className="flex w-full h-[100vh]">
      <p>{show}</p>
      <div className="w-[50vw] h-[80vh] bg-gray-200 rounded-2xl m-auto flex flex-col p-10">
        {viewList[0]}
      </div>
    </div>
  );
};

export default App2;
